// pages/Bills/List.js - 财务记录列表页
import React, { useEffect, useState } from 'react';
import { getFinancialRecords } from '../../api/bills';
import { useNavigate } from 'react-router-dom';

const BillsList = () => {
  const [records, setRecords] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();
  const [filters, setFilters] = useState({
    start: new Date(Date.now() - 30 * 24 * 60 * 60 * 1000).toISOString().split('T')[0], // 默认最近30天
    end: new Date().toISOString().split('T')[0],
    type: ''
  });

  const fetchRecords = async () => {
    try {
      setLoading(true);
      const params = {
        start: `${filters.start}T00:00:00`,
        end: `${filters.end}T23:59:59`,
        ...(filters.type && { type: filters.type })
      };
      const response = await getFinancialRecords(params);
      setRecords(response.data);
    } catch (error) {
      console.error('获取财务记录失败:', error);
      setError(error.response?.data?.message || '加载记录失败');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchRecords();
  }, [filters]);

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters(prev => ({ ...prev, [name]: value }));
  };

  if (loading) return <div>加载中...</div>;
  if (error) return <div>错误: {error}</div>;

  return (
    <div className="bills-page">
      <h1>财务记录</h1>
      
      {/* 筛选工具栏 */}
      <div className="filters">
        <div className="filter-group">
          <label>
            开始日期:
            <input
              type="date"
              name="start"
              value={filters.start}
              onChange={handleFilterChange}
              max={filters.end}
            />
          </label>
          <label>
            结束日期:
            <input
              type="date"
              name="end"
              value={filters.end}
              onChange={handleFilterChange}
              min={filters.start}
              max={new Date().toISOString().split('T')[0]}
            />
          </label>
          <label>
            交易类型:
            <select
              name="type"
              value={filters.type}
              onChange={handleFilterChange}
            >
              <option value="">全部</option>
              <option value="purchase">采购</option>
              <option value="sale">销售</option>
            </select>
          </label>
          <button onClick={fetchRecords}>刷新</button>
        </div>
      </div>

      <button onClick={() => navigate('/')}>返回主页</button>

      {/* 记录表格 */}
      <table className="records-table">
        <thead>
          <tr>
            <th>ID</th>
            <th>交易时间</th>
            <th>类型</th>
            <th>图书信息</th>
            <th>交易详情</th>
            <th>操作人</th>
          </tr>
        </thead>
        <tbody>
          {records.length > 0 ? (
            records.map(record => (
              <tr key={record.id} className={`record-${record.bill_type}`}>
                <td>{record.id}</td>
                <td>{new Date(record.created_at).toLocaleString()}</td>
                <td className={`type ${record.bill_type}`}>
                  {record.bill_type === 'purchase' ? '采购' : '销售'}
                </td>
                <td className="book-info">
                  <div><strong>{record.title}</strong></div>
                  <div>ISBN: {record.isbn}</div>
                  <div>ID: {record.bk_id}</div>
                  <div>{record.author} | {record.publisher}</div>
                </td>
                <td className="transaction-details">
                  <div>单价: ¥{record.price.toFixed(2)}</div>
                  <div>数量: {record.quantity}</div>
                  <div>总金额: ¥{record.amount.toFixed(2)}</div>
                </td>
                <td>
                  {record.user_id ? `用户ID: ${record.user_id}` : '系统操作'}
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="6" className="no-records">
                没有找到财务记录
              </td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};

export default BillsList;
