// pages/Orders/List.js - 订单列表页
import React, { useEffect, useState } from 'react';
import { getAllOrders, payOrder, returnOrder } from '../../api/orders';
import { useNavigate } from 'react-router-dom';
import '../../style/Orders.css';

const OrdersList = () => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [payingId, setPayingId] = useState(null); // 支付状态
  const [returnId, setReturnId] = useState(null); // 退款状态
  const navigate = useNavigate();

  useEffect(() => {
    const fetchOrders = async () => {
      try {
        setLoading(true);
        const response = await getAllOrders();
        setOrders(response.data);
      } catch (error) {
        console.error('获取订单失败:', error);
        setError(error.response?.data?.message || '加载订单失败');
      } finally {
        setLoading(false);
      }
    };
    fetchOrders();
  }, []);

  const handlePay = async (orderId) => {
    try {
      setPayingId(orderId);
      await payOrder(orderId);
      setOrders(orders.map(order =>
        order.id === orderId ? { ...order, order_status: 'paid' } : order
      ));
      alert('支付成功');
    } catch (error) {
      alert(error.response?.data?.message || '支付失败');
    } finally {
      setPayingId(null);
    }
  };

  const handleReturn = async (orderId) => {
    try {
      setReturnId(orderId);
      await returnOrder(orderId);
      setOrders(orders.map(order =>
        order.id === orderId ? { ...order, order_status: 'returned' } : order
      ));
      alert('退货成功');
    } catch (error) {
      alert(error.response?.data?.message || '退货失败');
    } finally {
      setReturnId(null);
    }
  };

  if (loading) return <div>加载订单中...</div>;
  if (error) return <div>错误: {error}</div>;

  return (
    <div className="orders-container">
      <div className="orders-header">
        <h1 className="page-title">采购订单列表</h1>
        <button onClick={() => navigate('/')} className="back-button">
          返回主页
        </button>
      </div>

      <div className="orders-toolbar">
        <button
          onClick={() => navigate('/orders/new')}
          className="create-order-button"
        >
          + 新建进货订单
        </button>
      </div>

      <div className="orders-table-container">
        <table>
          <thead>
            <tr>
              <th>订单ID</th>
              <th>图书信息</th>
              <th>采购信息</th>
              <th>状态</th>
              <th>时间</th>
              <th>操作</th>
            </tr>
          </thead>
          <tbody>
            {orders.map(order => (
              <tr key={order.id}>
                <td>#{order.id}</td>
                <td>
                  <div><strong>{order.title}</strong></div>
                  <div>ISBN: {order.isbn}</div>
                  <div>作者: {order.author}</div>
                  <div>出版社: {order.publisher}</div>
                  {order.bk_id && <div>图书ID: {order.bk_id}</div>}
                </td>
                <td>
                  <div>单价: ¥{order.purchase_price.toFixed(2)}</div>
                  <div>数量: {order.quantity}</div>
                  <div>总价: ¥{(order.purchase_price * order.quantity).toFixed(2)}</div>
                </td>
                <td>
                  <span className={`status ${order.order_status}`}>
                    {order.order_status === 'unpaid' ? '未支付' :
                      order.order_status === 'paid' ? '已支付' : '已退货'}
                  </span>
                  <div>入库: {order.is_stocked ? '✅' : '❌'}</div>
                </td>
                <td>
                  {new Date(order.created_at).toLocaleString()}
                </td>
                <td className="actions">
                  {/* 支付按钮（仅未支付订单显示） */}
                  {order.order_status === 'unpaid' && (
                    <button
                      disabled={payingId === order.id}
                      onClick={() => handlePay(order.id)}
                    >
                      {payingId === order.id ? '处理中...' : '支付订单'}
                    </button>
                  )}

                  {/* 入库按钮（已支付且未入库显示） */}
                  {order.order_status === 'paid' && !order.is_stocked && (
                    <button
                      onClick={() => navigate(`/orders/stock/${order.id}`)}
                      className="stock-btn"
                    >
                      图书入库
                    </button>
                  )}

                  {/* 创建新订单按钮（固定在表格外） */}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default OrdersList;
