import React, { useEffect, useState } from 'react';
import { getBooks, searchBooks } from '../../api/books';
import { useNavigate } from 'react-router-dom';
import BookCard from '../../components/BookCard';

const BooksList = ({ user }) => {
  const [books, setBooks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchKeyword, setSearchKeyword] = useState('');
  const navigate = useNavigate();

  const isAdmin = user && (user.role === 'admin' || user.role === 'super_admin');

  // 获取图书数据
  const fetchBooks = async (keyword = '') => {
    try {
      setLoading(true);
      const response = keyword
        ? await searchBooks(keyword)
        : await getBooks();
      setBooks(response.data);
    } catch (error) {
      console.error('获取图书失败:', error);
      setError(error.response?.data?.message || '加载图书失败');
    } finally {
      setLoading(false);
    }
  };

  // 初始加载
  useEffect(() => {
    fetchBooks();
  }, []);

  const handleSearch = () => {
    fetchBooks(searchKeyword);
  };

  const handleReset = () => {
    setSearchKeyword('');
    fetchBooks();
  };

  const handleKeyDown = (e) => {
    if (e.key === 'Enter') {
      e.preventDefault();
      handleSearch();
    }
  };

  if (loading) return <div>加载中...</div>;
  if (error) return <div>错误: {error}</div>;

  return (
    <div className="books-page">
      <div className="search-bar">
        <input
          type="text"
          placeholder="输入书名搜索"
          value={searchKeyword}
          onChange={(e) => setSearchKeyword(e.target.value)}
          onKeyDown={handleKeyDown} // 改用 onKeyDown
        />
        <button onClick={handleSearch}>搜索</button>
        <button onClick={handleReset}>显示全部</button>
        {isAdmin && (
          <button
            onClick={() => navigate('/books/new')}
            className="create-btn"
          >
            + 添加新书
          </button>
        )}
      </div>

      <h1>图书列表</h1>
      <div className="books-grid">
        {books.length > 0 ? (
          books.map(book => (
            <BookCard
              key={book.id}
              book={book}
              onBuy={() => navigate(`/sale/${book.id}`)}
              onEdit={() => navigate(`/books/edit/${book.id}`)}
              showEdit={isAdmin}
            />
          ))
        ) : (
          <div className="no-books">没有找到图书</div>
        )}
      </div>
    </div>
  );
};

export default BooksList;
