 *
 * @component
 * @param {{
 *   user: import('../types').User | null,
 *   onLogout: () => void
 * }} props
 * @returns {JSX.Element}
 */
const Home = ({ user, onLogout }) => {
  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-50 p-6">
      <div className="max-w-4xl mx-auto">
        {/* È°∂ÈÉ®Ê†è */}
        <header className="flex justify-between items-center mb-8">
          <div>
            <h1 className="text-3xl font-light text-indigo-800">
              Ê¨¢ËøéÂõûÊù•, <span className="font-semibold">{user?.username || 'ËÆøÂÆ¢'}</span>
            </h1>
            <p className="text-indigo-500 mt-1">ËØ∑ÈÄâÊã©ÊÇ®Ë¶ÅÊìç‰ΩúÁöÑÂäüËÉΩ</p>
          </div>
          <button 
            onClick={onLogout}
            className="px-6 py-2 rounded-lg text-indigo-700 bg-white border border-indigo-200 hover:bg-indigo-50 transition-colors shadow-sm"
          >
            ÈÄÄÂá∫ÁôªÂΩï
          </button>
        </header>

        {/* ÂØºËà™ËèúÂçï */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          {/* Âü∫Á°ÄËèúÂçïÈ°π */}
          <BigButton href="/books" icon="üìö">‰π¶Á±çÁÆ°ÁêÜ</BigButton>
          <BigButton href="/profile" icon="üë§">‰∏™‰∫∫ËµÑÊñô</BigButton>
          
          {/* ÁÆ°ÁêÜÂëòËèúÂçïÈ°π */}
          {(user?.role === 'admin' || user?.role === 'super_admin') && (
            <>
              <BigButton href="/orders" icon="üì¶">ËøõË¥ßËÆ¢ÂçïÁÆ°ÁêÜ</BigButton>
              <BigButton href="/bills" icon="üí∞">Ë¥¶ÂçïËÆ∞ÂΩï</BigButton>
            </>
          )}
          
          {/* Ë∂ÖÁ∫ßÁÆ°ÁêÜÂëòËèúÂçïÈ°π */}
          {user?.role === 'super_admin' && (
            <BigButton href="/users" icon="üë•">Áî®Êà∑ÁÆ°ÁêÜ</BigButton>
          )}
        </div>
      </div>
    </div>
  );
};

// Â§ßÊåâÈíÆÁªÑ‰ª∂
const BigButton = ({ href, icon, children }) => {
  return (
    <a 
      href={href}
      className="bg-white rounded-xl shadow-sm p-6 hover:shadow-md transition-all flex flex-col items-center justify-center text-center hover:bg-indigo-50 border border-transparent hover:border-indigo-100"
    >
      <span className="text-4xl mb-3">{icon}</span>
      <span className="text-lg font-medium text-indigo-800">{children}</span>
      <span className="mt-2 text-sm text-indigo-400 opacity-0 group-hover:opacity-100 transition-opacity">
        ÁÇπÂáªËøõÂÖ• ‚Üí
      </span>
    </a>
  );
};

export default Home;
