// pages/Sales/Sale.js - 图书销售页
import React, { useState } from 'react';
import { sellBook } from '../../api/sales';
import { useNavigate } from 'react-router-dom';
import { useParams } from 'react-router-dom';


const Sale = () => {
  const { bookId } = useParams(); // 获取路由参数
  const [quantity, setQuantity] = useState(1); // 默认改为1更合理
  const [saleRecord, setSaleRecord] = useState(null); // 新增状态存储销售记录
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  const handleChange = (e) => {
    const value = Math.max(1, parseInt(e.target.value) || 1); // 确保最小为1
    setQuantity(value);
  };

  const handleSale = async () => {
    setError(null);
    try {
      setLoading(true);
      const response = await sellBook(Number(bookId), Number(quantity));
      setSaleRecord(response.data); // 存储完整销售记录
    } catch (error) {
      console.error('购买失败:', error);
      if (error.response?.status === 422) {
        const errorDetails = error.response.data.detail;
        if (Array.isArray(errorDetails)) {
          // 将错误数组转换为可读字符串
          setError(errorDetails.map(e => `${e.loc.join('.')}: ${e.msg}`).join('\n'));
        } else {
          setError(errorDetails || '验证失败');
        }
      } else {
        setError(error.response?.data?.message || error.message || '销售失败，请重试');
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="sale-container" style={{ "text-align": "center" }}>
      <button onClick={() => navigate('/books')} className="back-button">返回图书列表</button>
      <h1>购买图书 #{bookId}</h1>

      {error && <div style={{ color: 'red', margin: '10px' }}>{error}</div>}

      <div className="sale-form">
        <label>
          购买数量:
          <input
            type="number"
            min="1"
            value={quantity}
            onChange={handleChange}
            disabled={loading}
          />
        </label>
        <button onClick={handleSale} disabled={loading}>
          {loading ? '处理中...' : '确认购买'}
        </button>
      </div>

      {/* 销售记录详情展示 */}
      {saleRecord && (
        <div className="sale-record">
          <h2>购买凭证</h2>
          <table>
            <tbody>
              <tr><th>交易ID</th><td>{saleRecord.id}</td></tr>
              <tr><th>交易类型</th><td>{saleRecord.bill_type}</td></tr>
              <tr><th>图书ID</th><td>{saleRecord.bk_id}</td></tr>
              <tr><th>ISBN</th><td>{saleRecord.isbn}</td></tr>
              <tr><th>书名</th><td>{saleRecord.title}</td></tr>
              <tr><th>作者</th><td>{saleRecord.author}</td></tr>
              <tr><th>出版社</th><td>{saleRecord.publisher}</td></tr>
              <tr><th>单价</th><td>¥{saleRecord.price.toFixed(2)}</td></tr>
              <tr><th>数量</th><td>{saleRecord.quantity}</td></tr>
              <tr><th>总金额</th><td>¥{saleRecord.amount.toFixed(2)}</td></tr>
              <tr><th>交易时间</th><td>{new Date(saleRecord.created_at).toLocaleString()}</td></tr>
              <tr><th>交易者ID</th><td>{saleRecord.user_id}</td></tr>
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default Sale;
