import React from 'react';

const BookCard = ({ book, onBuy, showEdit, onEdit }) => {
  return (
    <div className="book-card">
      <div className="book-header">
        <h3>{book.title}</h3>
        <span className="book-id">ID: {book.id}</span>
      </div>
      
      <div className="book-details">
        <p><strong>ISBN:</strong> {book.isbn}</p>
        <p><strong>作者:</strong> {book.author}</p>
        <p><strong>出版社:</strong> {book.publisher}</p>
        <p><strong>零售价:</strong> ¥{book.retail_price.toFixed(2)}</p>
        <p><strong>库存:</strong> {book.stock_quantity}</p>
        <p><strong>更新时间:</strong> {new Date(book.updated_at).toLocaleString()}</p>
      </div>

      <div className="book-actions">
        <button onClick={onBuy} className="buy-btn">
          购买
        </button>
        
        {showEdit && (
          <button onClick={onEdit} className="edit-btn">
            编辑
          </button>
        )}
      </div>
    </div>
  );
};

export default BookCard;
