// pages/Profile.js - 个人信息页

import React, { useEffect, useState } from 'react';
import { getProfile, updateProfile } from '../api/auth';

const Profile = ({ user }) => {
  const [profileData, setProfileData] = useState({ 
    username: '',
    email: '',
    full_name: '',
    gender: '',
    age: null ,
    id: null,
    role: 'user',
    is_active: true,
    created_at: null,
    password: ''
  });

  const [passwordConfirm, setPasswordConfirm] = useState(''); // 密码确认字段

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const response = await getProfile();
        setProfileData(response.data);
      } catch (error) {
        console.error(error);
      }
    };
    fetchProfile();
  }, []);

  const handleChange = (e) => {
    setProfileData({ ...profileData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // 密码验证
    if (profileData.password && profileData.password !== passwordConfirm) {
      alert("密码确认不匹配！");
      return;
    }

    try {
      const updateData = {
        username: profileData.username,
        email: profileData.email,
        full_name: profileData.full_name || null,
        gender: profileData.gender || null,
        age: profileData.age ? Number(profileData.age) : null,
        ...(profileData.password && { password: profileData.password }) // 条件添加密码字段
      };

      await updateProfile(updateData);
      alert('个人资料更新成功');

      setProfileData({...profileData, password: ''});
      setPasswordConfirm('');
    } catch (error) {
      console.error(error);
      alert(error.response?.data?.message || '更新失败');
    }
  };

  return (
    <div>
      <h1>用户个人资料</h1>
      <button onClick={() => navigate('/')}>返回主页</button>

      <div className="read-only-section">
        <p>用户ID: {profileData.id}</p>
        <p>账户状态: {profileData.is_active ? '活跃' : '非活跃'}</p>
        <p>角色: {profileData.role}</p>
        <p>注册日期: {profileData.created_at ? new Date(profileData.created_at).toLocaleDateString() : ''}</p>
      </div>

      <form onSubmit={handleSubmit}>
        <p>用户名: </p><input type="text" name="username" value={profileData.username} onChange={handleChange} required />
        <p>电子邮件: </p><input type="email" name="email" value={profileData.email} onChange={handleChange} required />
        <p>全名: </p><input type="text" name="full_name" value={profileData.full_name || ''} onChange={handleChange} placeholder="全名" />
        <p>性别: </p><select 
          name="gender" 
          value={profileData.gender || ''} 
          onChange={handleChange}
        >
          <option value="">请选择性别</option>
          <option value="male">男</option>
          <option value="female">女</option>
        </select>
        <p>年龄: </p><input
          type="number"
          name="age"
          value={profileData.age || ''}
          onChange={handleChange}
          placeholder="年龄"
          min="0"
        />

        <div style={{ marginTop: '15px', borderTop: '1px solid #eee', paddingTop: '15px' }}>
          <h3>更改密码（留空则保持当前密码）</h3>
          <input
            type="password"
            name="password"
            value={profileData.password}
            onChange={handleChange}
            placeholder="新密码"
          />
          <input
            type="password"
            name="passwordConfirm"
            value={passwordConfirm}
            onChange={(e) => setPasswordConfirm(e.target.value)}
            placeholder="确认新密码"
          />
        </div>

        <button type="submit">更新个人资料</button>
      </form>
    </div>
  );
};

export default Profile;
