// pages/Books/Edit.js - 编辑图书页面（管理员）

import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { getBookById, updateBook } from '../../api/books';

const EditBook = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [bookData, setBookData] = useState({ title: '', author: '', price: 0 });

  useEffect(() => {
    const fetchBook = async () => {
      try {
        const response = await getBookById(id);
        setBookData(response.data);
      } catch (error) {
        console.error(error);
      }
    };
    fetchBook();
  }, [id]);

  const handleChange = (e) => {
    setBookData({ ...bookData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await updateBook(id, bookData);
      alert('Book updated successfully');
      navigate('/books');
    } catch (error) {
      console.error(error);
      alert('Failed to update book');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input type="text" name="title" value={bookData.title} onChange={handleChange} required />
      <input type="text" name="author" value={bookData.author} onChange={handleChange} required />
      <input type="number" name="price" value={bookData.price} onChange={handleChange} required />
      <button type="submit">Update Book</button>
    </form>
  );
};

export default EditBook;
