// pages/Profile.js - 个人信息页

import React, { useEffect, useState } from 'react';
import { getProfile, updateProfile } from '../api/auth';

const Profile = ({ user }) => {
  const [profileData, setProfileData] = useState({ 
    username: '',
    email: '',
    full_name: '',
    gender: '',
    age: null ,
    id: null,
    role: 'user',
    is_active: true,
    created_at: null,
    password: ''
  });

  const [passwordConfirm, setPasswordConfirm] = useState(''); // 密码确认字段

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const response = await getProfile();
        setProfileData(response.data);
      } catch (error) {
        console.error(error);
      }
    };
    fetchProfile();
  }, []);

  const handleChange = (e) => {
    setProfileData({ ...profileData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // 密码验证
    if (profileData.password && profileData.password !== passwordConfirm) {
      alert("Password confirmation doesn't match!");
      return;
    }

    try {
      const updateData = {
        username: profileData.username,
        email: profileData.email,
        full_name: profileData.full_name || null,
        gender: profileData.gender || null,
        age: profileData.age ? Number(profileData.age) : null,
        ...(profileData.password && { password: profileData.password }) // 条件添加密码字段
      };

      await updateProfile(updateData);
      alert('Profile updated successfully');

      setProfileData({...profileData, password: ''});
      setPasswordConfirm('');
    } catch (error) {
      console.error(error);
      alert(error.response?.data?.message || 'Update failed');
    }
  };

  return (
    <div>
      <h1>User Profile</h1>

      <div className="read-only-section">
      <p>User ID: {profileData.id}</p>
      <p>Account Status: {profileData.is_active ? 'Active' : 'Inactive'}</p>
      <p>Role: {profileData.role}</p>
      <p>Member Since: {profileData.created_at ? new Date(profileData.created_at).toLocaleDateString() : ''}</p>
      </div>

      <form onSubmit={handleSubmit}>
        <p>User Name: </p><input type="text" name="username" value={profileData.username} onChange={handleChange} required />
        <p>Email: </p><input type="email" name="email" value={profileData.email} onChange={handleChange} required />
        <p>Full Name: </p><input type="text" name="full_name" value={profileData.full_name || ''} onChange={handleChange} placeholder="Full Name" />
        <p>Gender: </p><select 
          name="gender" 
          value={profileData.gender || ''} 
          onChange={handleChange}
        >
          <option value="">Select Gender</option>
          <option value="male">Male</option>
          <option value="female">Female</option>
        </select>
        <p>Age: </p><input
          type="number"
          name="age"
          value={profileData.age || ''}
          onChange={handleChange}
          placeholder="Age"
          min="0"
        />

        <div style={{ marginTop: '15px', borderTop: '1px solid #eee', paddingTop: '15px' }}>
          <h3>Change Password (leave blank to keep current)</h3>
          <input
            type="password"
            name="password"
            value={profileData.password}
            onChange={handleChange}
            placeholder="New Password"
          />
          <input
            type="password"
            name="passwordConfirm"
            value={passwordConfirm}
            onChange={(e) => setPasswordConfirm(e.target.value)}
            placeholder="Confirm New Password"
          />
        </div>

        <button type="submit">Update Profile</button>
      </form>
    </div>
  );
};

export default Profile;
