// pages/Users/CreateAdmin.js - 创建普通管理员页（超级管理员）

import React, { useState } from 'react';
import { createAdminUser } from '../../api/users';
import { useNavigate } from 'react-router-dom';

const CreateAdmin = () => {
  const [userData, setUserData] = useState({ 
    username: '', 
    email: '', 
    password: '', 
    role: 'admin'
  });
  const navigate = useNavigate();

  const handleChange = (e) => {
    setUserData({ ...userData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await createAdminUser(userData);
      alert('新建管理员成功');
      setUserData({ username: '', email: '', password: '' });
    } catch (error) {
      console.error(error);
      alert(error.response?.data?.message || '新建管理员失败');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <p><strong>用户名</strong><input type="text" name="username" placeholder="Username" onChange={handleChange} required minLength={3}/></p>
      <p><strong>邮箱</strong><input type="email" name="email" placeholder="Email" onChange={handleChange} required /></p>
      <p><strong>密码</strong><input type="password" name="password" placeholder="Password" onChange={handleChange} required minLength={8} /></p>
      <button type="submit">新建管理员</button>
      <button onClick={() => navigate('/users')}>返回</button>
    </form>
  );
};

export default CreateAdmin;
