// pages/Home.js - 首页

import React from 'react';

/**
 * Home component - 显示欢迎信息与导航菜单
 *
 * @component
 * @param {{
 *   user: import('../types').User | null,
 *   onLogout: () => void
 * }} props
 * @returns {JSX.Element}
 */
const Home = ({ user, onLogout }) => {
  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-50 p-6">
      <div className="max-w-4xl mx-auto">
        {/* 顶部栏 */}
        <header className="flex justify-between items-center mb-12">
          <h1 className="text-3xl font-light text-indigo-800">
            欢迎回来, <span className="font-semibold">{user?.username || '访客'}</span>
          </h1>
          <button 
            onClick={onLogout}
            className="px-4 py-2 rounded-lg text-indigo-700 border border-indigo-300 hover:bg-indigo-50 transition-colors"
          >
            退出登录
          </button>
        </header>

        {/* 导航菜单 */}
        <nav className="bg-white rounded-2xl shadow-sm p-6">
          <ul className="space-y-4">
            {/* 基础菜单项 */}
            <NavItem href="/books" icon="📚">书籍管理</NavItem>
            <NavItem href="/profile" icon="👤">个人资料</NavItem>
            
            {/* 管理员菜单项 */}
            {(user?.role === 'admin' || user?.role === 'super_admin') && (
              <>
                <NavItem href="/orders" icon="📦">进货订单管理</NavItem>
                <NavItem href="/bills" icon="💰">账单记录</NavItem>
              </>
            )}
            
            {/* 超级管理员菜单项 */}
            {user?.role === 'super_admin' && (
              <NavItem href="/users" icon="👥">用户管理</NavItem>
            )}
          </ul>
        </nav>
      </div>
    </div>
  );
};

// 辅助组件 - 导航项
const NavItem = ({ href, icon, children }) => {
  return (
    <li>
      <a 
        href={href}
        className="flex items-center p-4 rounded-xl hover:bg-indigo-50 transition-colors group"
      >
        <span className="text-2xl mr-4 group-hover:scale-110 transition-transform">{icon}</span>
        <span className="text-lg text-gray-700 group-hover:text-indigo-600 transition-colors">
          {children}
        </span>
        <span className="ml-auto text-indigo-400">→</span>
      </a>
    </li>
  );
};

export default Home;
