// pages/Users/List.js - 用户列表页（超级管理员）

import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { 
  getAllUsers, 
  getUserById, 
  getUserByUsername,
  toggleUserActiveStatus 
} from '../../api/users';

const UsersList = () => {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchMode, setSearchMode] = useState('all'); // 'all' | 'id' | 'username'
  const [searchInput, setSearchInput] = useState('');
  const navigate = useNavigate();

  // 通用数据获取函数
  const fetchData = async () => {
    try {
      setLoading(true);
      setError(null);
      
      let response;
      if (searchMode === 'id' && searchInput) {
        response = await getUserById(searchInput);
        setUsers([response.data]); // 单条结果转为数组
      } else if (searchMode === 'username' && searchInput) {
        response = await getUserByUsername(searchInput);
        setUsers([response.data]);
      } else {
        response = await getAllUsers();
        setUsers(response.data);
      }
    } catch (error) {
      console.error('Fetch error:', error);
      setError(error.response?.data?.message || 'Failed to fetch data');
      setUsers([]); // 清空结果
    } finally {
      setLoading(false);
    }
  };

  // 初始化加载和搜索条件变化时重新获取
  useEffect(() => {
    fetchData();
  }, [searchMode]); // 搜索模式改变时触发重新加载

  const handleSearch = () => {
    if ((searchMode === 'id' || searchMode === 'username') && !searchInput.trim()) {
      setError('Please enter search term');
      return;
    }
    fetchData();
  };

  const handleReset = () => {
    setSearchMode('all');
    setSearchInput('');
  };

  const handleToggleActive = async (userId, currentStatus) => {
    try {
      await toggleUserActiveStatus(userId);
      setUsers(users.map(user => 
        user.id === userId ? { ...user, is_active: !currentStatus } : user
      ));
    } catch (error) {
      console.error('Toggle status failed:', error);
      alert(error.response?.data?.message || 'Operation failed');
    }
  };

  return (
    <div className="users-list">
      <h1>用户管理</h1>
      <button onClick={() => navigate('/')}>返回主页</button>
      
      {/* 搜索控制区 */}
      <div className="search-controls">
        <div>
          <label>
            <input 
              type="radio" 
              checked={searchMode === 'all'} 
              onChange={() => setSearchMode('all')}
            />
            Show All
          </label>
          <label>
            <input
              type="radio"
              checked={searchMode === 'id'}
              onChange={() => setSearchMode('id')}
            />
            ID搜索
          </label>
          <label>
            <input
              type="radio"
              checked={searchMode === 'username'}
              onChange={() => setSearchMode('username')}
            />
            用户名搜索
          </label>
        </div>

        {(searchMode === 'id' || searchMode === 'username') && (
          <div className="search-input">
            <input
              placeholder={`Enter ${searchMode === 'id' ? 'User ID' : 'Username'}`}
              value={searchInput}
              onChange={(e) => setSearchInput(e.target.value)}
            />
            <button onClick={handleSearch}>Search</button>
            <button onClick={handleReset}>Reset</button>
          </div>
        )}
      </div>

      {/* 状态提示 */}
      {loading && <div className="loading">Loading...</div>}
      {error && <div className="error">{error}</div>}

      {/* 结果表格 */}
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>用户名</th>
            <th>邮箱</th>
            <th>角色</th>
            <th>账户状态</th>
            <th>操作</th>
          </tr>
        </thead>
        <tbody>
          {users.length > 0 ? (
            users.map(user => (
              <tr key={user.id}>
                <td>{user.id}</td>
                <td 
                  className="clickable"
                  onClick={() => navigate(`/users/${user.id}`)}
                >
                  {user.username}
                </td>
                <td>{user.email}</td>
                <td className={`role ${user.role}`}>{user.role}</td>
                <td className={`status ${user.is_active ? 'active' : 'inactive'}`}>
                  {user.is_active ? 'Active' : 'Inactive'}
                </td>
                <td>
                  <button onClick={() => navigate(`/users/edit/${user.id}`)}>
                    编辑
                  </button>
                  <button
                    onClick={() => handleToggleActive(user.id, user.is_active)}
                    className={user.is_active ? 'danger' : 'success'}
                  >
                    {user.is_active ? '失效' : '激活'}
                  </button>
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="6" className="no-data">
                {!loading && 'No users found'}
              </td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};

export default UsersList;
