// pages/Users/Edit.js - 编辑用户信息页面（超级管理员）

import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { getUserById, updateUser } from '../../api/users';

const EditUser = () => {
  const { userId } = useParams();
  const navigate = useNavigate();
  const [userData, setUserData] = useState({
    username: '',
    email: '',
    role: '',
    full_name: '',
    gender: '',
    age: null,
    is_active: true
  });

  const handleToggleActive = async () => {
    try {
      await toggleUserActiveStatus(userId);
      setUserData(prev => ({ ...prev, is_active: !prev.is_active }));
      alert(`User ${!userData.is_active ? 'activated' : 'deactivated'} successfully`);
    } catch (error) {
      console.error(error);
      alert('Failed to toggle user status');
    }
  };

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const response = await getUserById(userId);
        setUserData(response.data);
      } catch (error) {
        console.error(error);
      }
    };
    fetchUser();
  }, [userId]);

  const handleChange = (e) => {
    setUserData({ ...userData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const updateData = {
        username: userData.username,
        email: userData.email,
        full_name: userData.full_name || null,
        gender: userData.gender || null,
        age: userData.age ? Number(userData.age) : null,
        role: userData.role,
        ...(userData.password && { password: userData.password })
      };
      
      await updateUser(userId, updateData);
      alert('User updated successfully');
      navigate('/users');
    } catch (error) {
      console.error(error);
      alert(error.response?.data?.message || 'Failed to update user');
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        {/* 基础信息字段 */}
        <h2>修改用户信息</h2>
        <p><strong>用户名</strong><input type="text" name="username" value={userData.username} onChange={handleChange} required /></p>
        <p><strong>邮箱</strong><input type="email" name="email" value={userData.email} onChange={handleChange} required /></p>

        {/* 补充可选字段 */}
        <p><strong>全名</strong><input type="text" name="full_name" value={userData.full_name || ''} onChange={handleChange} placeholder="Full Name" /></p>
        <p><strong>性别</strong><select name="gender" value={userData.gender || ''} onChange={handleChange}>
          <option value="">Select Gender</option>
          <option value="male">Male</option>
          <option value="female">Female</option>
        </select></p>
        <p><strong>年龄</strong><input type="number" name="age" value={userData.age || ''} onChange={handleChange} placeholder="Age" min="0" /></p>

        {/* 角色选择 */}
        <p><strong>角色</strong><select name="role" value={userData.role} onChange={handleChange} required>
          <option value="user">User</option>
          <option value="admin">Admin</option>
        </select></p>

        {/* 密码更新（可选） */}
        <p><strong>密码</strong><input
          type="password"
          name="password"
          value={userData.password || ''}
          onChange={handleChange}
          placeholder="New Password (leave blank to keep current)"
        /></p>

        <button type="submit">更新用户信息</button>
      </form>

      {/* 激活状态切换 */}
      <button
        onClick={handleToggleActive}
      >
        {userData.is_active ? 'Deactivate User' : 'Activate User'}
      </button>
    </div>
  );
};

export default EditUser;
