// Register.js - 注册页

import React, { useState } from 'react';
import { register } from '../api/auth';

const Register = () => {
  const [userData, setUserData] = useState({ 
    username: '', 
    email: '', 
    password: '' 
  });
  
  const handleChange = (e) => {
    setUserData({ ...userData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!userData.username || !userData.email || !userData.password) {
      alert('Please fill in all the required fields');
      return;
    }

    try {
      await register({
        username: userData.username.trim(),  // 去除首尾空格
        email: userData.email.trim(),
        password: userData.password
      });
      alert('Registration successful');

      // 注册成功后可以清空表单
      setUserData({ username: '', email: '', password: '' });
    } catch (error) {
      console.error(error);
      alert(error.response?.data?.message || 'Registration fail, please try it again');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      {/* 用户名输入 */}
      <input
        type="text"
        name="username"
        placeholder="用户名（4-20位字符）"
        value={userData.username}
        onChange={handleChange}
        required
        minLength={4}  // 前端最小长度验证
        maxLength={20} // 前端最大长度验证
      />
      
      {/* 邮箱输入 */}
      <input
        type="email"
        name="email"
        placeholder="电子邮箱"
        value={userData.email}
        onChange={handleChange}
        required
        autoComplete="email" // 启用浏览器自动填充
      />
      
      {/* 密码输入 */}
      <input
        type="password"
        name="password"
        placeholder="密码（至少8位）"
        value={userData.password}
        onChange={handleChange}
        required
        minLength={8}       // 密码最小长度
        autoComplete="new-password" // 防止浏览器自动填充旧密码
      />
      
      {/* 提交按钮 */}
      <button type="submit">注册</button>
    </form>
  );
};

export default Register;
