// pages/Books/Edit.js - 编辑图书页面（管理员）

import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { getBookById, updateBook } from '../../api/books';

const EditBook = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [bookData, setBookData] = useState({ title: '', author: '', retail_price: 0, publisher: '' });

  useEffect(() => {
    const fetchBook = async () => {
      try {
        const response = await getBookById(id);
        setBookData(response.data);
      } catch (error) {
        console.error(error);
      }
    };
    fetchBook();
  }, [id]);

  const handleChange = (e) => {
    setBookData({ ...bookData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await updateBook(id, bookData);
      alert('Book updated successfully');
      navigate('/books');
    } catch (error) {
      console.error(error);
      alert('Failed to update book');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <p><strong>Book Title: </strong><input type="text" name="title" value={bookData.title} onChange={handleChange} required />  </p>
      <p><strong>Author: </strong><input type="text" name="author" value={bookData.author} onChange={handleChange} required />  </p>
      <p><strong>Retail Price: </strong><input type="number" name="price" value={bookData.retail_price} onChange={handleChange} required /> </p>
      <p><strong>Publisher: </strong><input type="text" name="publisher" value={bookData.publisher} onChange={handleChange} required /></p>
      <button type="submit">Update Book</button>
    </form>
  );
};

export default EditBook;
